#define int long long
int arr[14534838];//961//246016
/*
dp[i][0] = dp[i - 1][0/1/2]
dp[i][1] = dp[i - 1][0/1]
dp[i][2] = dp[i - 1][0/2]
NONONO
dp[i][0] = dp[i - 1][0] + 2 * dp[i - 1][1]
dp[i][1] = dp[i - 1][0] + dp[i - 1][2]
(1 2) * (dp[i - 1][0]) = (dp[i][0])
(1 1)   (dp[i - 1][1])   (dp[i][1])
*/
const int MAXN = 2;
const int mod = 1000000007;
struct matrix {
	long long arr[MAXN][MAXN];
	int n, m;
};
matrix mul(matrix a, matrix b) {
	matrix c;
	memset(c.arr, 0, sizeof c.arr);
	c.n = a.n;
	c.m = b.m;
	for (int i = 0; i < c.n; i++) {
		for (int j = 0; j < c.m; j++) {
			c.arr[i][j] = 0;
			for (int k = 0; k < a.m; k++) {
				c.arr[i][j] += a.arr[i][k] * b.arr[k][j];
				c.arr[i][j] = c.arr[i][j] % mod;
			}
		}
	}
	return c;
}
matrix qkpow(matrix a, int b) {
	matrix ans;
	ans.arr[0][0] = 1;
	ans.arr[1][1] = 1;
	ans.arr[0][1] = 0;
	ans.arr[1][0] = 0;
	ans.n = 2;
	ans.m = 2;
	while (b) {
		if (b & 1) {
			ans = mul(ans, a);
		}
		a = mul(a, a);
		b >>= 1;
	}
	return ans;
}
signed main() {
	memset(arr, -1, sizeof arr);
    matrix typ;
    typ.n = typ.m = 2;
//	typ.arr[0][0] = typ.arr[0][2] = typ.arr[1][0] = typ.arr[2][1] = 1;
//	typ.arr[0][1] = typ.arr[1][1] = typ.arr[1][2] = typ.arr[2][0] = typ.arr[2][2] = 0;
	typ.arr[0][0] = typ.arr[1][0] = typ.arr[1][1] = 1;
	typ.arr[0][1] = 2;
    int n;
    scanf("%lld", &n);
    if (n == 1) printf("3\n");
	else {
		matrix a = qkpow(typ, n - 1);
		matrix tmp;
		tmp.n = 2;
		tmp.m = 1;
		tmp.arr[0][0] = tmp.arr[1][0] = 1;
		matrix b = mul(a, tmp);
		printf("%lld\n", (b.arr[0][0] + b.arr[1][0] * 2) % mod);
	}
	return 0;
}


















































